package com.test2;

public class TestMathod01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("매서드의 시작");
		TestMathod01.veryone("수잔", 36);
		veryone("메리", 56);
		out();
		
		
		
	}

	// 매서드 이름은 내 마음대로 지어도 상관없다. everyone 이던 veryone이던 내 맘 !
	public static void veryone(String na, int ag) {
		
		System.out.println("나의 이름은 " + ag + " 이고," + " 나이는 " + na + " 입니다");
		
		
	}
	
	public static void out() {
		
		System.out.println("이제 시작이다") ;	
	}
	}
	

